          this.list[index].isShowLenderInput = true;
        } else if (i == index && type == 'main') {
          this.list[index].isShowMainInput = true;
        } else if (i == index && type == 'subject') {
          this.list[index].isShowSubjectInput = true;
        }
      }
    },
    hideInput(index, type) {
