import * as echarts from 'echarts';
export default {
  data() {
    return {
      myChart: {},
      // 
      series: []
    };
  },
  mounted() {
    // 基于准备好的dom，初始化echarts实例
    this.myChart = echarts.init(document.getElementById('line'));
    // 绘制图表
  },
  created() {
    setTimeout(() => {
      this.series = [{
        name: '每月折旧',
        type: 'line',
        data: [120, 132, 101, 134]
      }, {
        name: '每月报销',
        type: 'line',
        data: [220, 182, 191, 234]
      }];
    }, 2000);
  },
  watch: {
    series: {
      handler() {
        this.myChart.setOption({
          title: {
            text: 'ECharts 入门示例'
          },
          tooltip: {},
          xAxis: {
            type: 'category',
            boundaryGap: false,
            // x轴
            data: ['1月', '2月', '3月', '4月']
          },
          yAxis: {},
          series: this.series
        });
      },
      deep: true
    }
  }
};
