import Vue from 'vue'
import VueRouter from 'vue-router'

Vue.use(VueRouter)


// 这里定义非菜单栏的路由
const commonRoutes = [

  // 登录页面
  {
    path: '/login',
    component: () => import('@/views/login/Login.vue')
  },

  //注册页面
  {
    path: '/register',
    component: () => import('@/views/login/Rigister')
  },

  // 统计页面
  {
    path: '/statistics',
    component: () => import('@/views/statistics/Statistics.vue')
  }

]
// 这里定义菜单栏的路由
export const menuRoutes = [
  // 首页
  {
    path: '/home',
    component: () => import('@/layout/Layout.vue'),
    meta: { title: '首页', menu: true, icon: 'el-icon-s-platform' },
    children: [
      {
        // 等价于取父亲的path
        path: '',
        meta: { title: '首页', menu: true },
        component: () => import('@/views/home/Home.vue')
      }
    ]
  },
  //凭证管理
  {
    path: '/voucher',
    component: () => import('@/layout/Layout.vue'),
    meta: { title: '凭证管理', menu: true, icon: 'el-icon-s-platform' },
    children: [
      {
        // 等价于取父亲的path
        path: '/voucher/voucher',
        meta: { title: '凭证管理'},
        component: () => import('@/views/voucher/Voucher.vue')
      },
      {
        // 等价于取父亲的path
        path: '/voucher/invoice',
        meta: { title: '发票管理'},
        component: () => import('@/views/voucher/Invoice.vue'),
      }
    ]
  },
  //工资管理
  {
    path: '/wage',
    component: () => import('@/layout/Layout.vue'),
    meta: { title: '工资管理',menu:true, icon:'el-icon-s-tools'},
    children: [
      {
        path: '/wage/Wage',
        meta: { title: '工资表' },
        component: () => import('@/views/wage/Wage.vue'),
      },
      {
        path:'/wage/WageColumn',
        meta:{title:'工资项'},
        component:()=> import('@/views/wage/WageColumn.vue')
      }
    ]
  },
  //固定资产管理
  {
    path: '/property',
    component: () => import('@/layout/Layout.vue'),
    meta: { title: '固定资产管理',menu:true, icon:'el-icon-s-tools'},
    children: [
      {
        path: '/property/Property',
        meta: { title: '固定资产' },
        component: () => import('@/views/property/Property.vue'),
      },
      {
        path: '/property/depreecoation',
        meta: { title: '折旧明细表' },
        component: () => import('@/views/property/Depreecoation.vue'),
      }
    ]
  },
  //
  // 系统设置
  {
    path: '/system',
    component: () => import('@/layout/Layout.vue'),
    meta: { title: '系统设置',menu:true, icon:'el-icon-s-tools'},
    children: [
      {
        path: '',
        meta: { title: '系统设置' },
        component: () => import('@/views/system/System.vue'),
      }
    ]
  },



]

const router = new VueRouter({
  routes: [
    ...commonRoutes,
    ...menuRoutes,
    {
      path: '/*',
      component: () => import('@/views/error/error.vue')
    }
  ]
})

export default router
