import Vue from 'vue'
import VueRouter from 'vue-router'

Vue.use(VueRouter)


// 这里定义非菜单栏的路由
const commonRoutes = [

  // 登录页面
  {
    path: '/login',
    component: () => import('@/views/login/Login.vue')
  },

  //注册页面
  {
    path: '/register',
    component: () => import('@/views/login/Rigister')
  },

  // 统计页面
  {
    path: '/statistics',
    component: () => import('@/views/statistics/Statistics.vue')
  }

]
// 这里定义菜单栏的路由
export const menuRoutes = [
  // 首页
  {
    path: '/home',
    component: () => import('@/layout/Layout.vue'),
    meta: { title: '首页', menu: true, icon: 'el-icon-s-platform' },
    children: [
      {
        // 等价于取父亲的path
        path: '',
        meta: { title: '首页', menu: true },
        component: () => import('@/views/home/Home.vue')
      }
    ]
  },
  //凭证管理
  {
    path: '/voucher',
    component: () => import('@/layout/Layout.vue'),
    meta: { title: '凭证管理', menu: true, icon: 'el-icon-postcard' },
    children: [
      {
        // 等价于取父亲的path
        path: '/voucher/whichVoucher',
        component: () => import('@/views/voucher/Voucher'),
        meta: { title: '凭证管理' },
      },
      {
        path: '/voucher/VoucherWord',
        component: () => import('@/views/voucher/VoucherWord'),
        meta: { title: '凭证字' },
      },
      {
        path: '/voucher/Summary',
        component: () => import('@/views/voucher/Summary'),
        meta: { title: '常用摘要' },
      },

    ]
  },
  //工资管理
  {
    path: '/Salary',
    component: () => import('@/layout/Layout.vue'),
    meta: { title: '工资管理', menu: true, icon: 'el-icon-bank-card' },
    children: [
      {
        path: '/Salary/salary',
        meta: { title: '工资表' },
        component: () => import('@/views/salary/Salary.vue'),
      },
      {
        path: '/Salary/salarycolumn',
        meta: { title: '工资项' },
        component: () => import('@/views/salary/SalaryColumn.vue')
      }
    ]
  },
  //固定资产管理
  {
    path: '/property',
    component: () => import('@/layout/Layout.vue'),
    meta: { title: '固定资产管理', menu: true, icon: 'el-icon-office-building' },
    children: [
      {
        path: '/property/Property',
        meta: { title: '固定资产' },
        component: () => import('@/views/property/Property.vue'),
      },
      {
        path: '/property/depreciation',
        meta: { title: '折旧明细表' },
        component: () => import('@/views/property/Depreciation.vue'),
      }
    ]
  },
  //资金管理
  {
    path: '/Invoice',
    component: () => import('@/layout/Layout.vue'),
    meta: { title: '发票管理', menu: true, icon: 'el-icon-s-goods' },
    children: [
      {
        path: '/Invoice/EnterInvoice',
        meta: { title: '进项发票' },
        component: () => import('@/views/Invoice/EnterInvoice.vue'),
      },
      {
        path: '/Invoice/OutInvoice',
        meta: { title: '销项发票' },
        component: () => import('@/views/Invoice/OutInvoice.vue'),
      },
      {
        path: '/Invoice/AddInvoice',
        meta: { title: '新增发票' },
        component: () => import('@/views/Invoice/AddInvoice.vue'),
      }
    ]
  },
  //期末结转
  {
    path: '/carryover',
    component: () => import('@/layout/Layout.vue'),
    meta: { title: '期末结转', menu: true, icon: 'el-icon-s-marketing' },
    children: [
      {
        path: '',
        meta: { title: '期末结转' },
        component: () => import('@/views/carryover/CarryOver'),
      },
    ]
  },

  // 系统设置
  {
    path: '/system',
    component: () => import('@/layout/Layout.vue'),
    meta: { title: '系统设置', menu: true, icon: 'el-icon-s-tools' },
    children: [
      {
        path: '',
        meta: { title: '系统设置' },
        component: () => import('@/views/system/System.vue'),
      }
    ]
  },



]

const router = new VueRouter({
  routes: [
    ...commonRoutes,
    ...menuRoutes,
    {
      path: '/*',
      component: () => import('@/views/error/error.vue')
    }
  ]
})

export default router
